import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux'
// import { Link } from 'react-router-dom'
import { handleProduct } from '../redux/action'
import "../styles/Products.css"
const Products = (props) => {

  const [products, setproducts] = useState(props.products)
  useEffect(() => {
    console.log('running')
    props.handleProduct()

  }, [])


  console.log(props)



  const renderList = () => {
    return this.props.products.products?.map((product) => {
      return (<>
        <div className=" box" onClick={handleProduct} key={product.code_num + "hi"}>
          <img src={product.images[1]} alt={product.name} />
          <div className="product-name">
            <small> {product.name}</small>
          </div>
          <div className="price"> &#8358;{product.price} </div>
          <div className="stars">
            <i className="fas fa-star"></i>
            <i className="fas fa-star"></i>
            <i className="fas fa-star"></i>
            <i className="fas fa-star"></i>
            <i className="fas fa-star-half-alt"></i>
          </div>
          <a className="btn">add to cart</a>
        </div>
      </>)
    })
  }

  return (
    <>
      <aside className="displaySearchResult" id="displaySearchResult">
        {
          // renderList()
          // console.log(this.props)
        }
      </aside>
    </>
  )

}

const mapStateToProps = (state) => {
  return { products: state.products }
}
export default connect(
  mapStateToProps, { handleProduct }
)(Products)
