import React from 'react'
import "../styles/SignUp.css"
const SignUp = () => {
  const handleContentLoaded = () => {
    const _ = (selector) => {
      return document.querySelector(selector);
    };
    //showing sign In page

    // const signInButton = _("#signInbtn");
    const modal = _("#signIn");
    const account = _('#account')

    const signUpPageLink = _('#signUpPage')
    modal.style.top = '-100%'
    _("#signUp").style.right = '-200%'


    _('.signInBtn').addEventListener("click", function () {
      modal.animate([
        {
          top: '-100%',
        },
        {
          top: '10%',
        }
      ],
        {
          duration: 500,
          easing: 'ease-in-out',
          direction: 'normal',
          fill: 'forwards'


        })
    })
    _(".SignInCloseBtn").addEventListener("click", function (event) {

      modal.animate([
        {
          top: '-100%',
        },
        {
          top: '10%',
        }
      ],
        {
          duration: 500,
          easing: 'ease-in-out',
          direction: 'reverse',
          fill: 'forwards'
        }
      )



    });


    //showing signUp page

    signUpPageLink.addEventListener("click", function (event) {


      modal.animate([
        {
          left: '0%',
        },
        {
          left: '-200%',
        }
      ],
        {
          duration: 500,
          easing: 'ease-in-out',
          direction: 'normal',
          fill: 'forwards'
        }
      )

      _("#signUp").animate([
        {
          right: '-200%',
        },
        {
          right: '0%',
        }
      ],
        {
          duration: 500,
          easing: 'ease-in-out',
          direction: 'normal',
          fill: 'forwards'
        }
      )



    });
    _('.SignUpCloseBtn').addEventListener("click", function (event) {


      modal.animate([
        {
          left: '0%',
        },
        {
          left: '-200%',
        }
      ],
        {
          duration: 500,
          easing: 'ease-in-out',
          direction: 'reverse',
          fill: 'forwards'
        }
      )

      _("#signUp").animate([
        {
          right: '-200%',
        },
        {
          right: '0%',
        }
      ],
        {
          duration: 500,
          easing: 'ease-in-out',
          direction: 'reverse',
          fill: 'forwards'
        }
      )



    });
    _('#signInLink').addEventListener("click", function (event) {


      modal.animate([
        {
          left: '0%',
        },
        {
          left: '-200%',
        }
      ],
        {
          duration: 500,
          easing: 'ease-in-out',
          direction: 'reverse',
          fill: 'forwards'
        }
      )

      _("#signUp").animate([
        {
          right: '-200%',
        },
        {
          right: '0%',
        }
      ],
        {
          duration: 500,
          easing: 'ease-in-out',
          direction: 'reverse',
          fill: 'forwards'
        }
      )



    });

  };

  useEffect(() => {
    console.log("using effect");
    document.addEventListener("DOMContentLoaded", handleContentLoaded);
  }, []);

  return (
    <>

      {/* <!--SIGN UP MODAL--> */}
      <form class="signUp" id="signUp">
        <span class="SignUpCloseBtn">&times;</span>
        <div class="signUp__heading">
          <h3>register</h3>
        </div>
        <div class="signUp__formInput">
          <input type="email" placeholder="Enter email" id="signUpEmail" />
          <input type="text" id="first_name" placeholder="First Name" />
          <input type="text" id="last_name" placeholder="last Name" />
          <input type="text" id="middle_name" placeholder="Middle Name" />
          <input type="number" placeholder="+234" id="phone" />
          <input type="password" placeholder="Enter password" id="password" />
        </div>

        <div class="signIns">
          <div class="sign-up">
            <a href="#" id="sign-up-btn">
              Sign Up
            </a>
          </div>
          <div>- OR -</div>
          <div class="signUp__choices">
            <div class="signUp__choices__google">
              <button> Sign Up google</button>
            </div>
          </div>
        </div>
        <p>By proceeding, you are agreeing to our <span><a href="#">Terms & condditions</a></span> and <span><a
          href="#">Privacy Policy</a></span></p>
        <p>Already a user ? <span><a id="signInLink" href="#signIn">Sign In here</a></span></p>

      </form>
    </>
  )
}

export default SignUp
