import action from '../action'

const init = {
  data: [],

}

const reducer = (state = init, action) => {
  switch (action.type) {
    case 'FETCH_PRODUCTS':
      return {
        ...state,
        data: action.payload
      }
    default:
      return state
  }


}

export default reducer
