import action from '../action'

const init = {
  data: [],

}

const reducer = (state = init, action) => {
  if (action.type === 'FETCH_PRODUCTS') {
    return {
      ...state,
      data: action.payload
    }
  }

  return state

  switch (action.type) {
    case 'FETCH_PRODUCTS':
      return {
        ...state,
        data: action.payload
      }
      break;

    default:
      break;
  }


}

export default reducer
