import iconicWebApp from "../Api/iconicWebApp"
const FETCH_PRODUCTS = 'FETCH_PRODUCTS'


export const handleProduct = (url) => async dispatch => {
  const response = await iconicWebApp({
    method: "POST", url: url, data: {
      query: "medical",
      category: null,
      min_price: null,
      max_price: null,

    }
  });
  dispatch({
    type: FETCH_PRODUCTS,
    payload: response.data
  })
}
