import React, { Component } from 'react'
import { connect } from 'react-redux'
import { handleProduct } from '../redux/action'
import "../styles/Products.css"
class Products extends Component {

  componentDidMount() {
    this.props.handleProduct()
  }


  render() {
    this.state = this.props.products
    // console.log(this.state.products.data)
    return (

      <>
        <aside class="displaySearchResult" id="displaySearchResult">


          <>
            <div class=" box" onClick={handleProduct}>
              <img src="images/asset 11.jpeg" alt="" />
              <div class="product-name">
                <small>Guyton and Hall Textbook of Medical Physiology (Guyton Physiology) 14th Edition</small>
              </div>
              <div class="price"> &#8358; 7,000 </div>
              <div class="stars">
                <i class="fas fa-star"></i>
                <i class="fas fa-star"></i>
                <i class="fas fa-star"></i>
                <i class="fas fa-star"></i>
                <i class="fas fa-star-half-alt"></i>
              </div>
              <a href="#" class="btn">add to cart</a>
            </div>
          </>


        </aside>
      </>
    )
  }
}

const mapStateToProps = (state) => {
  return { products: state.products }
}
export default connect(
  mapStateToProps, { handleProduct }
)(Products)
