import iconicWebApp from "../Api/iconicWebApp"
const FETCH_PRODUCTS = 'FETCH_PRODUCTS'


export const handleProduct = () => async (dispatch, getState) => {
  const response = await iconicWebApp.get("/products/")
  dispatch({
    type: FETCH_PRODUCTS,
    payload: response
  })
}
